//import React
import React, { Component } from "react";
//import Form, Button from semantic-ui-react
import { Form, Button, Input, TextArea } from "semantic-ui-react";
//import loyout
import Layout from "../../componets/layout";

//class based componet
class NewDreamStory extends Component {
  //state for form input and error message
  state = {
    title: "",
    story: "",
    min_down_price: "",
    error_msg: "",
    loading: false
  };
  //render
  render() {
    return (
      <Layout>
        <Form>
          <Form.Group>
            <Form.Field width={12}>
              <label>Create a Dream Story</label>
              <Input
                placeholder="Title"
                value={this.state.title}
                onChange={event => this.setState({ title: event.target.value })}
              ></Input>
            </Form.Field>
            <Form.Field width={4}>
              <label>Minimum Download Price</label>
              <Input
                label="ether"
                labelPosition="right"
                placeholder="0.001"
                value={this.state.min_down_price}
                onChange={event =>
                  this.setState({ min_down_price: event.target.value })
                }
              ></Input>
            </Form.Field>
          </Form.Group>
          <Form.Group>
            <Form.Field width={12}>
              <TextArea
                label="Creat a Dream Story"
                placeholder="Tell us about your dream story"
                style={{ minHeight: 300 }}
                value={this.state.story}
                onChange={event => this.setState({ story: event.target.value })}
              ></TextArea>
            </Form.Field>
          </Form.Group>
          <Button primary>Create!</Button>
        </Form>
      </Layout>
    );
  }
}

export default NewDreamStory;
